import { useState, useEffect } from "react";
import { v4 as uuidv4 } from "uuid";
import { useNavigate } from "react-router-dom";

export default function AdminDashboard() {
  const [books, setBooks] = useState([
    { id: 1, title: "The Great Gatsby", author: "F. Scott Fitzgerald", category: "Fiction", ISBN: "12375" },
    { id: 2, title: "1984", author: "George Orwell", category: "Fiction", ISBN: "67890" },
    { id: 3, title: "The Alchemist", author: "Paulo Coelho", category: "Fiction", ISBN: "12345" },
    { id: 4, title: "Clean Code", author: "Robert C. Martin", category: "Programming", ISBN: "67890" },
    { id: 5, title: "Atomic Habits", author: "James Clear", category: "Self-help", ISBN: "54321" },
    { id: 6, title: "The Lean Startup", author: "Eric Ries", category: "Business", ISBN: "09876" },
    { id: 7, title: "Sapiens", author: "Yuval Noah Harari", category: "History", ISBN: "13579" },
    { id: 8, title: "The Da Vinci Code", author: "Dan Brown", category: "Mystery", ISBN: "24680" },
    { id: 9, title: "The Subtle Art of Not Giving a F*ck", author: "Mark Manson", category: "Self-help", ISBN: "97531" },
  ]);

  const [searchQuery, setSearchQuery] = useState("");
  const [filteredBooks, setFilteredBooks] = useState(books);

  const navigate = useNavigate();

  useEffect(() => {
    if (!localStorage.getItem("admin")) {
      navigate("/admin-login");
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("admin");
    navigate("/");
  };

  // Handle Book Search
  useEffect(() => {
    const query = searchQuery.toLowerCase();
    const results = books.filter(
      (book) =>
        book.title.toLowerCase().includes(query) ||
        book.author.toLowerCase().includes(query) ||
        book.category.toLowerCase().includes(query) ||
        book.ISBN.includes(query)
    );
    setFilteredBooks(results);
  }, [searchQuery, books]);

  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [category, setCategory] = useState("");
  const [isbn, setIsbn] = useState("");

  const addBook = () => {
    if (title && author && category && isbn) {
      const newBook = { id: uuidv4(), title, author, category, ISBN: isbn };
      setBooks([...books, newBook]);
      setTitle("");
      setAuthor("");
      setCategory("");
      setIsbn("");
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Admin Dashboard</h1>
      <button onClick={handleLogout}>Home Page</button>

      {/* Search Books Section */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Search by Title, Author, Category, ISBN..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{ padding: "10px", width: "100%", maxWidth: "400px" }}
        />
      </div>

      {/* Add Book Section */}
      <div className="mb-6 p-4 border rounded">
        <h2 className="text-xl font-semibold mb-2">Add a New Book</h2>
        <div className="flex gap-2">
          <input placeholder="Book Title" value={title} onChange={(e) => setTitle(e.target.value)} />
          <input placeholder="Author" value={author} onChange={(e) => setAuthor(e.target.value)} />
          <input placeholder="Category" value={category} onChange={(e) => setCategory(e.target.value)} />
          <input placeholder="ISBN" value={isbn} onChange={(e) => setIsbn(e.target.value)} />
          <button onClick={addBook}>Add Book</button>
        </div>
      </div>

      {/* Book Management Section */}
      <h2 className="text-xl font-semibold mb-2">Manage Books</h2>
      <table className="w-full border-collapse border">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Category</th>
            <th>ISBN</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredBooks.length > 0 ? (
            filteredBooks.map((book) => (
              <tr key={book.id}>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.category}</td>
                <td>{book.ISBN}</td>
                <td>
                  <button>Edit</button>
                  <button>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No books found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
