// src/AdminProductManagement.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./ProductCatalogue.css";

const AdminProductManagement = () => {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const storedProducts = JSON.parse(localStorage.getItem("products")) || [];
    setProducts(storedProducts);
  }, []);

  const handleAddProduct = () => {
    const newProduct = {
      id: products.length + 1,
      name: prompt("Enter product name:"),
      price: prompt("Enter product price:"),
      image: prompt("Enter product image URL:")
    };

    if (newProduct.name && newProduct.price && newProduct.image) {
      const updatedProducts = [...products, newProduct];
      setProducts(updatedProducts);
      localStorage.setItem("products", JSON.stringify(updatedProducts));
    }
  };

  const handleEditProduct = (index) => {
    const updatedProducts = [...products];
    updatedProducts[index].name = prompt("Update product name:", updatedProducts[index].name);
    updatedProducts[index].price = prompt("Update product price:", updatedProducts[index].price);
    updatedProducts[index].image = prompt("Update product image URL:", updatedProducts[index].image);
    
    setProducts(updatedProducts);
    localStorage.setItem("products", JSON.stringify(updatedProducts));
  };

  const handleDeleteProduct = (index) => {
    const updatedProducts = products.filter((_, i) => i !== index);
    setProducts(updatedProducts);
    localStorage.setItem("products", JSON.stringify(updatedProducts));
  };

  return (
    <div className="product-container">
      <h2>Admin Product Management</h2>
      <button className="cart-button" onClick={handleAddProduct}>
        Add Product
      </button>

      <div className="product-list">
        {products.map((product, index) => (
          <div key={product.id} className="product-item">
            <img src={product.image} alt={product.name} className="product-image" />
            <p className="product-name">{product.name}</p>
            <p className="product-price">{product.price}</p>
            <button className="edit-button" onClick={() => handleEditProduct(index)}>Edit</button>
            <button className="delete-button" onClick={() => handleDeleteProduct(index)}>Delete</button>
          </div>
        ))}
      </div>

      <button className="logout-button" onClick={() => navigate("/")}>
        Logout
      </button>
    </div>
  );
};

export default AdminProductManagement;
